require("@rails/activestorage").start()
require("channels")

var voters_count

function change() {
    voters_count = document.getElementsByName('number_of_candidates')[0].value;

    var i = 0;
    for (i = 0; i < voters_count; i++) {
        var input = document.createElement('input');
        input.type = "test";
        input.name = "name of candidate " + (i + 1)
        input.placeholder = "name of candidate " + (i + 1);
        document.getElementById('name').appendChild(input);
        var br = document.createElement('br');
        document.getElementById('name').appendChild(br);
    }
}

